import React, { useMemo, useState, useEffect } from 'react';
import { useReactTable, getCoreRowModel, flexRender } from '@tanstack/react-table';
import { getPositions, getTechniqueTypes, addCustomFieldToTechnique, deleteCustomFieldFromTechnique, getTechniques, deleteTechnique } from '../services/api';

const TechniqueTable = ({ updateTechnique }) => {
    const [techniquesState, setTechniquesState] = useState([]);
    const [isEditing, setIsEditing] = useState(null);
    const [editedTechnique, setEditedTechnique] = useState(null);
    const [positions, setPositions] = useState([]);
    const [techniqueTypes, setTechniqueTypes] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [showDeleteModal, setShowDeleteModal] = useState(false);
    const [showDeleteTechniqueModal, setShowDeleteTechniqueModal] = useState(false);
    const [newFieldType, setNewFieldType] = useState('');
    const [newFieldName, setNewFieldName] = useState('');
    const [fieldToDelete, setFieldToDelete] = useState(null);
    const [techniqueToDelete, setTechniqueToDelete] = useState(null);

    useEffect(() => {
        const fetchTechniques = async () => {
            try {
                const data = await getTechniques();
                setTechniquesState(data);
            } catch (error) {
                console.error('Error al obtener técnicas:', error);
            }
        };

        const fetchPositions = async () => {
            try {
                const data = await getPositions();
                setPositions(data);
            } catch (error) {
                console.error('Error al obtener posiciones:', error);
            }
        };

        const fetchTechniqueTypes = async () => {
            try {
                const data = await getTechniqueTypes();
                setTechniqueTypes(data);
            } catch (error) {
                console.error('Error al obtener tipos de técnica:', error);
            }
        };

        fetchTechniques();
        fetchPositions();
        fetchTechniqueTypes();
    }, []);

    const getPositionName = (id) => {
        const position = positions.find((pos) => pos._id === id);
        return position ? position.name : 'Sin posición';
    };

    const getTechniqueTypeName = (idArray) => {
        const names = idArray
            .map((id) => {
                const type = techniqueTypes.find((t) => t._id === id);
                return type ? type.name : 'Sin tipo';
            })
            .join(', ');
        return names;
    };

    const columns = useMemo(() => {
        const baseColumns = [
            { id: 'name', accessorKey: 'name', header: 'Nombre' },
            {
                id: 'techniqueType',
                accessorKey: 'techniqueType',
                header: 'Tipo',
                cell: ({ getValue }) => getTechniqueTypeName(getValue()),
            },
            {
                id: 'positionStart',
                accessorKey: 'positionStart',
                header: 'Posición Inicial',
                cell: ({ getValue }) => getPositionName(getValue()),
            },
            {
                id: 'endPosition',
                accessorKey: 'endPosition',
                header: 'Posición Final',
                cell: ({ getValue }) => getPositionName(getValue()),
            },
            { id: 'proficiencyLevel', accessorKey: 'proficiencyLevel', header: 'Proficiency Level' },
            { id: 'giNoGi', accessorKey: 'giNoGi', header: 'Gi/No Gi' },
            {
                id: 'actions',
                header: 'Acciones',
                cell: ({ row }) => (
                    <>
                        <button onClick={() => handleEdit(row.original)}>Editar</button>
                        <button
                            onClick={() => handleDeleteTechniqueClick(row.original._id)}
                            style={styles.deleteButton}
                        >
                            Eliminar
                        </button>
                    </>
                ),
            },
        ];

        const customFieldColumns = [];
        if (techniquesState.length > 0) {
            const customFields = Object.keys(techniquesState[0].customFields || {});
            customFieldColumns.push(
                ...customFields.map((field) => ({
                    id: field,
                    accessorKey: `customFields.${field}`,
                    header: (
                        <div>
                            {field.charAt(0).toUpperCase() + field.slice(1)}
                            <button onClick={() => handleDeleteFieldClick(field)} style={styles.deleteFieldButton}>🗑️</button>
                        </div>
                    ),
                    cell: ({ getValue }) => getValue() || 'N/A',
                }))
            );
        }

        return [
            ...baseColumns,
            ...customFieldColumns,
            {
                id: 'addNewField',
                header: (
                    <button onClick={() => setShowModal(true)} style={styles.addNewFieldButton}>
                        Add New Field
                    </button>
                ),
                cell: () => null,
            },
        ];
    }, [positions, techniqueTypes, techniquesState]);

    const table = useReactTable({
        data: techniquesState,
        columns,
        getCoreRowModel: getCoreRowModel(),
    });

    const handleEdit = (technique) => {
        setIsEditing(technique._id);
        setEditedTechnique({ ...technique });
    };

    const handleSave = () => {
        updateTechnique(editedTechnique);
        setIsEditing(null);
    };

    const handleCancel = () => {
        setIsEditing(null);
        setEditedTechnique(null);
    };

    const handleAddNewField = async () => {
        try {
            await addCustomFieldToTechnique(newFieldName, newFieldType);
            setShowModal(false);
            setNewFieldName('');
            setNewFieldType('');
            const updatedTechniques = await getTechniques();
            setTechniquesState(updatedTechniques);
        } catch (error) {
            console.error('Error al agregar nuevo campo:', error);
        }
    };

    const handleDeleteFieldClick = (field) => {
        setFieldToDelete(field);
        setShowDeleteModal(true);
    };

    const handleDeleteTechniqueClick = (techniqueId) => {
        setTechniqueToDelete(techniqueId);
        setShowDeleteTechniqueModal(true);
    };

    const handleConfirmDeleteField = async () => {
        if (!fieldToDelete) {
            console.error("Error: fieldToDelete está vacío o no definido.");
            return;
        }

        try {
            await deleteCustomFieldFromTechnique(fieldToDelete);
            setShowDeleteModal(false);
            setFieldToDelete(null);
            const updatedTechniques = await getTechniques();
            setTechniquesState(updatedTechniques);
        } catch (error) {
            console.error('Error al eliminar campo personalizado:', error);
        }
    };

    return (
        <div>
            <h1>Techniques</h1>
            <table style={styles.table}>
                <thead>
                    {table.getHeaderGroups().map((headerGroup) => (
                        <tr key={headerGroup.id}>
                            {headerGroup.headers.map((header) => (
                                <th key={header.id} style={styles.cell}>
                                    {flexRender(header.column.columnDef.header, header.getContext())}
                                </th>
                            ))}
                        </tr>
                    ))}
                </thead>
                <tbody>
                    {table.getRowModel().rows.map((row) => (
                        <tr key={row.id}>
                            {row.getVisibleCells().map((cell) => (
                                <td key={cell.id} style={styles.cell}>
                                    {flexRender(cell.column.columnDef.cell, cell.getContext())}
                                </td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>

            {showModal && (
                <div style={styles.modalOverlay}>
                    <div style={styles.modal}>
                        <h2>Agregar nuevo campo</h2>
                        <input
                            type="text"
                            placeholder="Nombre del campo"
                            value={newFieldName}
                            onChange={(e) => setNewFieldName(e.target.value)}
                            style={styles.input}
                        />
                        <select
                            value={newFieldType}
                            onChange={(e) => setNewFieldType(e.target.value)}
                            style={styles.input}
                        >
                            <option value="">Selecciona el tipo de dato</option>
                            <option value="string">Texto</option>
                            <option value="number">Número</option>
                            <option value="rating">Calificación (1 a 10)</option>
                            <option value="boolean">Checklist</option>
                        </select>
                        <button onClick={handleAddNewField} style={styles.saveButton}>
                            Agregar
                        </button>
                        <button onClick={() => setShowModal(false)} style={styles.closeButton}>
                            Cancelar
                        </button>
                    </div>
                </div>
            )}

            {showDeleteModal && (
                <div style={styles.modalOverlay}>
                    <div style={styles.modal}>
                        <h2>¿Eliminar el campo "{fieldToDelete}"?</h2>
                        <button onClick={handleConfirmDeleteField} style={styles.deleteButton}>
                            Confirmar
                        </button>
                        <button onClick={() => setShowDeleteModal(false)} style={styles.closeButton}>
                            Cancelar
                        </button>
                    </div>
                </div>
            )}

            {showDeleteTechniqueModal && (
                <div style={styles.modalOverlay}>
                    <div style={styles.modal}>
                        <h2>¿Estás seguro de que deseas eliminar esta técnica?</h2>
                        <button onClick={handleConfirmDeleteTechnique} style={styles.deleteButton}>
                            Confirmar
                        </button>
                        <button onClick={() => setShowDeleteTechniqueModal(false)} style={styles.closeButton}>
                            Cancelar
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

