import React, { useMemo } from 'react';
import { useReactTable, getCoreRowModel, flexRender } from '@tanstack/react-table';

const TechniqueTable = ({ techniques, updateTechnique, deleteTechnique }) => {
    const handleEdit = (technique) => {
        console.log('Editando técnica:', technique);
        // Aquí puedes implementar la lógica para editar la técnica
    };

    const columns = useMemo(
        () => [
            { accessorKey: 'name', header: 'Nombre' },
            {
                accessorKey: 'techniqueType', header: 'Tipo',
                cell: info => info.getValue().join(', ')
            },
            { accessorKey: 'positionStart', header: 'Posición Inicial' },
            { accessorKey: 'endPosition', header: 'Posición Final' },
            { accessorKey: 'proficiencyLevel', header: 'Nivel de Pericia' },
            { accessorKey: 'giNoGi', header: 'Gi/No Gi' },
            {
                accessorKey: 'actions',
                header: 'Acciones',
                cell: ({ row }) => (
                    <div>
                        <button onClick={() => handleEdit(row.original)}>Editar</button>
                        <button
                            onClick={() => deleteTechnique(row.original._id)}
                            style={styles.deleteButton}
                        >
                            Eliminar
                        </button>
                    </div>
                ),
            },
        ],
        [deleteTechnique]
    );

    const table = useReactTable({
        data: techniques,
        columns,
        getCoreRowModel: getCoreRowModel(),
    });

    return (
        <table style={styles.table}>
            <thead>
                {table.getHeaderGroups().map(headerGroup => (
                    <tr key={headerGroup.id}>
                        {headerGroup.headers.map(header => (
                            <th key={header.id}>
                                {flexRender(
                                    header.column.columnDef.header,
                                    header.getContext()
                                )}
                            </th>
                        ))}
                    </tr>
                ))}
            </thead>
            <tbody>
                {table.getRowModel().rows.map(row => (
                    <tr key={row.id}>
                        {row.getVisibleCells().map(cell => (
                            <td key={cell.id}>
                                {flexRender(cell.column.columnDef.cell, cell.getContext())}
                            </td>
                        ))}
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

const styles = {
    table: {
        width: '100%',
        borderCollapse: 'collapse',
        tableLayout: 'auto',
    },
    deleteButton: {
        color: 'red',
    },
};

export default TechniqueTable;
