import React from 'react';
import { useTable } from '@tanstack/react-table';

const PositionTable = ({ positions }) => {
    const data = React.useMemo(() => positions, [positions]);

    const columns = React.useMemo(
        () => [
            { accessorKey: 'name', header: 'Nombre' },
            { accessorKey: 'description', header: 'Descripci贸n' },
            {
                accessorKey: 'duality', header: 'Dualidad',
                cell: ({ row }) =>
                    row.original.duality
                        ? `${row.original.duality.element1}/${row.original.duality.element2}`
                        : 'No especificada'
            },
            {
                accessorKey: 'scenariosIf', header: 'Escenarios IF',
                cell: ({ row }) =>
                    row.original.scenariosIf.length > 0 ? (
                        <ul>
                            {row.original.scenariosIf.map(ifScenario => (
                                <li key={ifScenario._id}>{ifScenario.description || 'Sin descripci贸n'}</li>
                            ))}
                        </ul>
                    ) : 'No hay escenarios IF'
            },
            { accessorKey: 'proficiencyLevel', header: 'Proficiency Level' },
            { accessorKey: 'giNoGi', header: 'Gi/NoGi' },
            {
                accessorKey: 'notesPage', header: 'Notas',
                cell: ({ row }) => row.original.notesPage || 'Sin notas'
            },
        ],
        []
    );

    const table = useTable({ data, columns });

    return (
        <table>
            <thead>
                {table.getHeaderGroups().map(headerGroup => (
                    <tr key={headerGroup.id}>
                        {headerGroup.headers.map(header => (
                            <th key={header.id}>{header.isPlaceholder ? null : header.renderHeader()}</th>
                        ))}
                    </tr>
                ))}
            </thead>
            <tbody>
                {table.getRowModel().rows.map(row => (
                    <tr key={row.id}>
                        {row.getVisibleCells().map(cell => (
                            <td key={cell.id}>{cell.renderCell()}</td>
                        ))}
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default PositionTable;
