import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TechniqueTypeForm from './TechniqueTypeForm';
import PositionForm from './PositionForm';
import IfEscenarioForm from './IfEscenarioForm';
import { getTechniqueCustomFields } from '../services/api';

const TechniqueForm = ({ addTechnique }) => {
    const [name, setName] = useState('');
    const [techniqueType, setTechniqueType] = useState('');
    const [positionStart, setPositionStart] = useState('');
    const [temporalPositions, setTemporalPositions] = useState([]);
    const [endPosition, setEndPosition] = useState('');
    const [ifScenarios, setIfScenarios] = useState('');
    const [media, setMedia] = useState('');
    const [externalLinks, setExternalLinks] = useState('');
    const [relatedPositions, setRelatedPositions] = useState([]);
    const [relatedTechniques, setRelatedTechniques] = useState('');
    const [proficiencyLevel, setProficiencyLevel] = useState(1);
    const [giNoGi, setGiNoGi] = useState('both');
    const [practiceToday, setPracticeToday] = useState(false);
    const [positionsList, setPositionsList] = useState([]);
    const [techniquesList, setTechniquesList] = useState([]);
    const [techniqueTypesList, setTechniqueTypesList] = useState([]);
    const [ifsList, setIfsList] = useState([]);
    const [TechniqueCustomFields, setTechniqueCustomFields] = useState({});
    const [customFields, setCustomFields] = useState([]); // Para almacenar los customFields del backend
    const [showTechniqueTypeForm, setShowTechniqueTypeForm] = useState(false);
    const [showPositionForm, setShowPositionForm] = useState(false);
    const [showIfForm, setShowIfForm] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [positionsResponse, techniquesResponse, typesResponse, ifsResponse, customFieldsResponse] = await Promise.all([
                    axios.get('http://localhost:5000/positions'),
                    axios.get('http://localhost:5000/techniques'),
                    axios.get('http://localhost:5000/techniquetypes'),
                    axios.get('http://localhost:5000/ifescenarios'),
                    getTechniqueCustomFields() // Obtenemos los campos personalizados del backend
                ]);
                setPositionsList(positionsResponse.data);
                setTechniquesList(techniquesResponse.data);
                setTechniqueTypesList(typesResponse.data);
                setIfsList(ifsResponse.data);
                setCustomFields(customFieldsResponse); // Guardamos los customFields
            } catch (error) {
                console.error('Error al obtener datos:', error);
            }
        };
        fetchData();
    }, []);

    const handleAddTemporalPosition = () => {
        setTemporalPositions([...temporalPositions, '']);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const techniqueData = {
            name,
            techniqueType,
            positionStart,
            temporalPositions,
            endPosition,
            ifScenarios,
            media: media.split(',').map((m) => m.trim()),
            externalLinks: externalLinks.split(',').map((link) => link.trim()),
            relatedPositions,
            relatedTechniques,
            proficiencyLevel,
            giNoGi,
            practiceToday,
            TechniqueCustomFields,
        };
        addTechnique(techniqueData)
            .then(() => {
                setName('');
                setTechniqueType('');
                setPositionStart('');
                setTemporalPositions([]);
                setEndPosition('');
                setIfScenarios('');
                setMedia('');
                setExternalLinks('');
                setRelatedPositions([]);
                setRelatedTechniques('');
                setProficiencyLevel(1);
                setGiNoGi('both');
                setPracticeToday(false);
                setTechniqueCustomFields({});
            })
            .catch((error) => {
                console.error('Error al agregar la técnica:', error);
            });
    };

    // Renderiza cada campo personalizado dependiendo de su tipo
    const renderCustomFieldInput = (field) => {
        const { _id, name, type, options } = field;

        switch (type) {
            case 'Text':
                return (
                    <input
                        type="text"
                        placeholder={name}
                        value={TechniqueCustomFields[_id] || ''}
                        onChange={(e) => setTechniqueCustomFields({ ...TechniqueCustomFields, [_id]: e.target.value })}
                        style={styles.inputField}
                    />
                );
            case 'Number':
                return (
                    <input
                        type="number"
                        placeholder={name}
                        value={TechniqueCustomFields[_id] || ''}
                        onChange={(e) => setTechniqueCustomFields({ ...TechniqueCustomFields, [_id]: e.target.value })}
                        style={styles.inputField}
                    />
                );
            case 'Select':
                return (
                    <select
                        value={TechniqueCustomFields[_id] || ''}
                        onChange={(e) => setTechniqueCustomFields({ ...TechniqueCustomFields, [_id]: e.target.value })}
                        style={styles.selectField}
                    >
                        <option value="">Select {name}</option>
                        {options.map((opt, idx) => (
                            <option key={idx} value={opt}>
                                {opt}
                            </option>
                        ))}
                    </select>
                );
            case 'Multiselect':
                return (
                    <select
                        multiple
                        value={TechniqueCustomFields[_id] || []}
                        onChange={(e) => {
                            const selectedOptions = Array.from(
                                e.target.selectedOptions,
                                (option) => option.value
                            );
                            setTechniqueCustomFields({ ...TechniqueCustomFields, [_id]: selectedOptions });
                        }}
                        style={styles.selectField}
                    >
                        {options.map((opt, idx) => (
                            <option key={idx} value={opt}>
                                {opt}
                            </option>
                        ))}
                    </select>
                );
            case 'Date':
                return (
                    <input
                        type="date"
                        value={TechniqueCustomFields[_id] || ''}
                        onChange={(e) => setTechniqueCustomFields({ ...TechniqueCustomFields, [_id]: e.target.value })}
                        style={styles.inputField}
                    />
                );
            case 'Hyperlink':
                return (
                    <input
                        type="url"
                        placeholder={name}
                        value={TechniqueCustomFields[_id] || ''}
                        onChange={(e) => setTechniqueCustomFields({ ...TechniqueCustomFields, [_id]: e.target.value })}
                        style={styles.inputField}
                    />
                );
            case 'Multimedia':
                return (
                    <input
                        type="file"
                        onChange={(e) => setTechniqueCustomFields({ ...TechniqueCustomFields, [_id]: e.target.files })}
                        style={styles.inputField}
                    />
                );
            default:
                return null;
        }
    };

    return (
        <form onSubmit={handleSubmit} style={styles.formContainer}>
            {/* Campos Predeterminados */}
            <label style={styles.label}>Nombre de la Técnica:</label>
            <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
                style={styles.inputField}
            />

            {/* Otros campos predeterminados... */}
            {/* Custom Fields Expansibles */}
            <details>
                <summary>Campos Personalizados</summary>
                {customFields.map((field) => (
                    <div key={field._id} style={styles.customFieldContainer}>
                        <label style={styles.label}>{field.name}:</label>
                        {renderCustomFieldInput(field)}
                    </div>
                ))}
            </details>

            <button type="submit" style={styles.submitButton}>Añadir Técnica</button>

            {/* Formularios Modulares */}
            {showTechniqueTypeForm && <TechniqueTypeForm onClose={() => setShowTechniqueTypeForm(false)} />}
            {showPositionForm && <PositionForm onClose={() => setShowPositionForm(false)} />}
            {showIfForm && <IfEscenarioForm onClose={() => setShowIfForm(false)} />}
        </form>
    );
};

const styles = {
    formContainer: {
        display: 'flex',
        flexDirection: 'column',
        maxWidth: '500px',
        padding: '1rem',
        border: '1px solid #ddd',
        borderRadius: '8px',
        boxShadow: '0 0 10px rgba(0,0,0,0.1)',
    },
    label: {
        marginBottom: '0.3rem',
        fontWeight: 'bold',
    },
    inputField: {
        marginBottom: '1rem',
        padding: '0.5rem',
        width: '100%',
        borderRadius: '4px',
        border: '1px solid #ddd',
    },
    selectField: {
        marginBottom: '1rem',
        padding: '0.5rem',
        width: '100%',
        borderRadius: '4px',
        border: '1px solid #ddd',
    },
    submitButton: {
        marginTop: '1rem',
        padding: '0.5rem 1rem',
        backgroundColor: '#4CAF50',
        color: '#fff',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
    },
    customFieldContainer: {
        marginBottom: '1rem',
    },
};

export default TechniqueForm;
