import React from 'react';
import { createColumnHelper, flexRender, getCoreRowModel, useReactTable } from '@tanstack/react-table';

const columnHelper = createColumnHelper();

const PositionTable = ({ positions }) => {
    const columns = [
        columnHelper.accessor('name', { header: 'Nombre' }),
        columnHelper.accessor('description', {
            header: 'Descripción',
            cell: info => info.getValue() || 'Sin descripción'
        }),
        columnHelper.accessor('duality', {
            header: 'Dualidad',
            cell: info =>
                info.row.original.duality
                    ? `${info.row.original.duality.element1}/${info.row.original.duality.element2}`
                    : 'No especificada'
        }),
        columnHelper.accessor('scenariosIf', {
            header: 'Escenarios IF',
            cell: info =>
                info.getValue().length > 0 ? (
                    <ul>
                        {info.getValue().map((scenario) => (
                            <li key={scenario._id}>{scenario.description || 'Sin descripción'}</li>
                        ))}
                    </ul>
                ) : 'No hay escenarios IF'
        }),
        columnHelper.accessor('proficiencyLevel', { header: 'Proficiency Level' }),
        columnHelper.accessor('giNoGi', { header: 'Gi/NoGi' }),
        columnHelper.accessor('notesPage', {
            header: 'Notas',
            cell: info => info.getValue() || 'Sin notas'
        }),
    ];

    const table = useReactTable({
        data: positions,
        columns,
        getCoreRowModel: getCoreRowModel(),
    });

    return (
        <table>
            <thead>
                {table.getHeaderGroups().map(headerGroup => (
                    <tr key={headerGroup.id}>
                        {headerGroup.headers.map(header => (
                            <th key={header.id}>
                                {header.isPlaceholder ? null : flexRender(header.column.columnDef.header, header.getContext())}
                            </th>
                        ))}
                    </tr>
                ))}
            </thead>
            <tbody>
                {table.getRowModel().rows.map(row => (
                    <tr key={row.id}>
                        {row.getVisibleCells().map(cell => (
                            <td key={cell.id}>
                                {flexRender(cell.column.columnDef.cell, cell.getContext())}
                            </td>
                        ))}
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default PositionTable;
