import React, { useMemo, useState } from 'react';
import { useTable, usePagination } from '@tanstack/react-table';

const TechniqueTable = ({ techniques, updateTechnique, deleteTechnique }) => {
    const [isEditing, setIsEditing] = useState(null);
    const [editedTechnique, setEditedTechnique] = useState(null);

    const columns = useMemo(() => [
        { Header: 'Nombre', accessor: 'name' },
        {
            Header: 'Tipo', accessor: 'techniqueType',
            Cell: ({ value }) => value.join(', ')
        },
        { Header: 'Posici贸n Inicial', accessor: 'positionStart' },
        { Header: 'Posici贸n Final', accessor: 'endPosition' },
        { Header: 'Proficiency Level', accessor: 'proficiencyLevel' },
        { Header: 'Gi/No Gi', accessor: 'giNoGi' },
        {
            Header: 'Acciones', accessor: 'actions',
            Cell: ({ row }) => (
                <>
                    <button onClick={() => handleEdit(row.original)}>Editar</button>
                    <button onClick={() => deleteTechnique(row.original._id)} style={styles.deleteButton}>
                        Eliminar
                    </button>
                </>
            )
        },
    ], []);

    const tableInstance = useTable(
        { columns, data: techniques },
        usePagination
    );

    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = tableInstance;

    const handleEdit = (technique) => {
        setIsEditing(technique._id);
        setEditedTechnique({ ...technique });
    };

    const handleCancel = () => {
        setIsEditing(null);
        setEditedTechnique(null);
    };

    const handleSave = () => {
        updateTechnique(editedTechnique);
        setIsEditing(null);
    };

    return (
        <table {...getTableProps()} style={styles.table}>
            <thead>
                {headerGroups.map((headerGroup) => (
                    <tr {...headerGroup.getHeaderGroupProps()}>
                        {headerGroup.headers.map((column) => (
                            <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                        ))}
                    </tr>
                ))}
            </thead>
            <tbody {...getTableBodyProps()}>
                {rows.map((row) => {
                    prepareRow(row);
                    return (
                        <tr {...row.getRowProps()}>
                            {row.cells.map((cell) => (
                                <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                            ))}
                        </tr>
                    );
                })}
            </tbody>
        </table>
    );
};

const styles = {
    table: {
        width: '100%',
        borderCollapse: 'collapse',
    },
    deleteButton: {
        color: 'red',
    },
};

export default TechniqueTable;
