import React, { useState, useEffect } from 'react';
import { useTable } from '@tanstack/react-table';
import axios from 'axios'; // Asegúrate de tener axios instalado

const TechniqueTable = ({ positions }) => {
    const [techniques, setTechniques] = useState([]);
    const [isEditing, setIsEditing] = useState(null);
    const [editedTechnique, setEditedTechnique] = useState(null);

    useEffect(() => {
        const fetchTechniques = async () => {
            try {
                const response = await axios.get('/api/techniques');
                setTechniques(response.data);
            } catch (error) {
                console.error('Error al obtener técnicas:', error);
            }
        };

        fetchTechniques();
    }, []);

    const getPositionName = (positionId) => {
        const position = positions.find((pos) => pos._id === positionId);
        return position ? position.name : 'Sin posición';
    };

    const handleEdit = (technique) => {
        setIsEditing(technique._id);
        setEditedTechnique({ ...technique });
    };

    const handleCancel = () => {
        setIsEditing(null);
        setEditedTechnique(null);
    };

    const handleSave = async () => {
        try {
            await axios.put(`/api/techniques/${editedTechnique._id}`, editedTechnique);
            setTechniques((prev) =>
                prev.map((tech) =>
                    tech._id === editedTechnique._id ? editedTechnique : tech
                )
            );
            setIsEditing(null);
        } catch (error) {
            console.error('Error al guardar la técnica:', error);
        }
    };

    const columns = [
        { Header: 'Nombre', accessor: 'name' },
        {
            Header: 'Tipo',
            accessor: (row) => row.techniqueType.join(', '),
        },
        {
            Header: 'Posición Inicial',
            accessor: (row) => getPositionName(row.positionStart),
        },
        {
            Header: 'Posición Final',
            accessor: (row) => getPositionName(row.endPosition),
        },
        { Header: 'Proficiency Level', accessor: 'proficiencyLevel' },
        { Header: 'Gi/No Gi', accessor: 'giNoGi' },
        {
            Header: 'Acciones',
            accessor: 'actions',
            Cell: ({ row }) =>
                isEditing === row.original._id ? (
                    <>
                        <button onClick={handleSave}>Guardar</button>
                        <button onClick={handleCancel}>Cancelar</button>
                    </>
                ) : (
                    <>
                        <button onClick={() => handleEdit(row.original)}>Editar</button>
                        <button
                            onClick={() => deleteTechnique(row.original._id)}
                            style={{ color: 'red' }}
                        >
                            Eliminar
                        </button>
                    </>
                ),
        },
    ];

    const table = useTable({ columns, data: techniques });

    return (
        <table {...table.getTableProps()} style={styles.table}>
            <thead>
                {table.headerGroups.map((headerGroup) => (
                    <tr {...headerGroup.getHeaderGroupProps()}>
                        {headerGroup.headers.map((column) => (
                            <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                        ))}
                    </tr>
                ))}
            </thead>
            <tbody {...table.getTableBodyProps()}>
                {table.rows.map((row) => {
                    table.prepareRow(row);
                    return (
                        <tr {...row.getRowProps()}>
                            {row.cells.map((cell) => (
                                <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                            ))}
                        </tr>
                    );
                })}
            </tbody>
        </table>
    );
};

const styles = {
    table: {
        width: '100%',
        borderCollapse: 'collapse',
    },
};

export default TechniqueTable;
