import React, { useState } from 'react';

const IfEscenarioForm = ({ addIfEscenario }) => {
    const [description, setDescription] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!description) {
            alert('La descripci칩n es requerida');
            return;
        }

        const newIfEscenario = { description };

        try {
            await addIfEscenario(newIfEscenario);
            setDescription('');
        } catch (error) {
            console.error('Error al crear el escenario IF:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>Descripci칩n del Escenario IF:</label>
            <input
                type="text"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                required
            />
            <button type="submit">A침adir IF</button>
        </form>
    );
};

export default IfEscenarioForm;
