import React, { useMemo, useState, useEffect } from 'react';
import { useReactTable, getCoreRowModel, flexRender } from '@tanstack/react-table';
import { getTechniques, getPositions, getTechniqueTypes, addCustomFieldToTechnique } from '../services/api';

const TechniqueTable = ({ initialTechniques, updateTechnique, deleteTechnique }) => {
    const [isEditing, setIsEditing] = useState(null);
    const [editedTechnique, setEditedTechnique] = useState(null);
    const [positions, setPositions] = useState([]);
    const [techniqueTypes, setTechniqueTypes] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [newFieldType, setNewFieldType] = useState('');
    const [newFieldName, setNewFieldName] = useState('');
    const [techniquesState, setTechniquesState] = useState(initialTechniques); // Renombrado para evitar confusión

    useEffect(() => {
        const fetchPositions = async () => {
            try {
                const data = await getPositions();
                setPositions(data);
            } catch (error) {
                console.error('Error al obtener posiciones:', error);
            }
        };
        fetchPositions();
    }, []);

    useEffect(() => {
        const fetchTechniqueTypes = async () => {
            try {
                const data = await getTechniqueTypes();
                setTechniqueTypes(data);
            } catch (error) {
                console.error('Error al obtener tipos de técnica:', error);
            }
        };
        fetchTechniqueTypes();
    }, []);

    const getPositionName = (id) => {
        const position = positions.find((pos) => pos._id === id);
        return position ? position.name : 'Sin posición';
    };

    const getTechniqueTypeName = (idArray) => {
        const names = idArray
            .map((id) => {
                const type = techniqueTypes.find((t) => t._id === id);
                return type ? type.name : 'Sin tipo';
            })
            .join(', ');
        return names;
    };

    const columns = useMemo(
        () => [
            { id: 'name', accessorKey: 'name', header: 'Nombre' },
            {
                id: 'techniqueType',
                accessorKey: 'techniqueType',
                header: 'Tipo',
                cell: ({ getValue }) => getTechniqueTypeName(getValue()),
            },
            {
                id: 'positionStart',
                accessorKey: 'positionStart',
                header: 'Posición Inicial',
                cell: ({ getValue }) => getPositionName(getValue()),
            },
            {
                id: 'endPosition',
                accessorKey: 'endPosition',
                header: 'Posición Final',
                cell: ({ getValue }) => getPositionName(getValue()),
            },
            { id: 'proficiencyLevel', accessorKey: 'proficiencyLevel', header: 'Proficiency Level' },
            { id: 'giNoGi', accessorKey: 'giNoGi', header: 'Gi/No Gi' },
            {
                id: 'actions',  // Agregar id para esta columna
                header: 'Acciones',
                cell: ({ row }) => (
                    <>
                        <button onClick={() => handleEdit(row.original)}>Editar</button>
                        <button
                            onClick={() => deleteTechnique(row.original._id)}
                            style={styles.deleteButton}
                        >
                            Eliminar
                        </button>
                    </>
                ),
            },
            {
                id: 'addNewField',  // Agregar id aquí también
                header: (
                    <button onClick={() => setShowModal(true)} style={styles.addNewFieldButton}>
                        Add New Field
                    </button>
                ),
                cell: () => null, // No hay datos en esta celda para las técnicas existentes
            },
        ],
        [positions, techniqueTypes]
    );

    const table = useReactTable({
        data: techniquesState,
        columns,
        getCoreRowModel: getCoreRowModel(),
    });

    const handleEdit = (technique) => {
        setIsEditing(technique._id);
        setEditedTechnique({ ...technique });
    };

    const handleSave = () => {
        updateTechnique(editedTechnique);
        setIsEditing(null);
    };

    const handleCancel = () => {
        setIsEditing(null);
        setEditedTechnique(null);
    };

    const handleAddNewField = async () => {
        try {
            // Llama al backend para agregar el campo personalizado
            await addCustomFieldToTechnique(newFieldName, newFieldType);
            setShowModal(false);
            setNewFieldName('');
            setNewFieldType('');

            // Vuelve a cargar las técnicas para reflejar el nuevo campo
            const updatedTechniques = await getTechniques();
            setTechniques(updatedTechniques); // Esto asegura que los datos estén actualizados

        } catch (error) {
            console.error('Error al agregar nuevo campo:', error);
        }
    };

    return (
        <div>
            <h1>Techniques</h1>
            <table style={styles.table}>
                <thead>
                    {table.getHeaderGroups().map((headerGroup) => (
                        <tr key={headerGroup.id}>
                            {headerGroup.headers.map((header) => (
                                <th key={header.id} style={styles.cell}>
                                    {flexRender(header.column.columnDef.header, header.getContext())}
                                </th>
                            ))}
                        </tr>
                    ))}
                </thead>
                <tbody>
                    {table.getRowModel().rows.map((row) => (
                        <tr key={row.id}>
                            {row.getVisibleCells().map((cell) => (
                                <td key={cell.id} style={styles.cell}>
                                    {flexRender(cell.column.columnDef.cell, cell.getContext())}
                                </td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>

            {showModal && (
                <div style={styles.modalOverlay}>
                    <div style={styles.modal}>
                        <h2>Agregar nuevo campo</h2>
                        <input
                            type="text"
                            placeholder="Nombre del campo"
                            value={newFieldName}
                            onChange={(e) => setNewFieldName(e.target.value)}
                            style={styles.input}
                        />
                        <select
                            value={newFieldType}
                            onChange={(e) => setNewFieldType(e.target.value)}
                            style={styles.input}
                        >
                            <option value="">Selecciona el tipo de dato</option>
                            <option value="string">Texto</option>
                            <option value="number">Número</option>
                            <option value="rating">Calificación (1 a 10)</option>
                            <option value="boolean">Checklist</option>
                        </select>
                        <button onClick={handleAddNewField} style={styles.saveButton}>
                            Agregar
                        </button>
                        <button onClick={() => setShowModal(false)} style={styles.closeButton}>
                            Cancelar
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

const styles = {
    table: {
        width: '100%',
        borderCollapse: 'collapse',
        border: '1px solid black',
    },
    cell: {
        textAlign: 'left',
        border: '1px solid black',
        padding: '8px',
    },
    deleteButton: {
        color: 'red',
    },
    addNewFieldButton: {
        color: 'blue',
    },
    modalOverlay: {
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.5)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
    },
    modal: {
        backgroundColor: 'white',
        padding: '2rem',
        borderRadius: '5px',
        width: '300px',
    },
    input: {
        width: '100%',
        marginBottom: '1rem',
        padding: '0.5rem',
    },
    saveButton: {
        marginRight: '0.5rem',
    },
    closeButton: {
        color: 'red',
    },
};

export default TechniqueTable;
