import React, { useState, useEffect } from 'react';
import { useReactTable, flexRender, getCoreRowModel } from '@tanstack/react-table';
import axios from 'axios';

const TechniqueTable = ({ positions }) => {
    const [techniques, setTechniques] = useState([]);
    const [isEditing, setIsEditing] = useState(null);
    const [editedTechnique, setEditedTechnique] = useState(null);

    useEffect(() => {
        const fetchTechniques = async () => {
            try {
                const response = await axios.get('/api/techniques');
                setTechniques(response.data);
            } catch (error) {
                console.error('Error al obtener técnicas:', error);
            }
        };

        fetchTechniques();
    }, []);

    const getPositionName = (positionId) => {
        const position = positions.find((pos) => pos._id === positionId);
        return position ? position.name : 'Sin posición';
    };

    const handleEdit = (technique) => {
        setIsEditing(technique._id);
        setEditedTechnique({ ...technique });
    };

    const handleCancel = () => {
        setIsEditing(null);
        setEditedTechnique(null);
    };

    const handleSave = async () => {
        try {
            await axios.put(`/api/techniques/${editedTechnique._id}`, editedTechnique);
            setTechniques((prev) =>
                prev.map((tech) =>
                    tech._id === editedTechnique._id ? editedTechnique : tech
                )
            );
            setIsEditing(null);
        } catch (error) {
            console.error('Error al guardar la técnica:', error);
        }
    };

    const deleteTechnique = async (id) => {
        try {
            await axios.delete(`/api/techniques/${id}`);
            setTechniques((prev) => prev.filter((tech) => tech._id !== id));
        } catch (error) {
            console.error('Error al eliminar la técnica:', error);
        }
    };

    const columns = [
        { accessorKey: 'name', header: 'Nombre' },
        {
            accessorKey: 'techniqueType', header: 'Tipo',
            cell: info => info.getValue().join(', ')
        },
        {
            accessorKey: 'positionStart', header: 'Posición Inicial',
            cell: info => getPositionName(info.getValue())
        },
        {
            accessorKey: 'endPosition', header: 'Posición Final',
            cell: info => getPositionName(info.getValue())
        },
        { accessorKey: 'proficiencyLevel', header: 'Proficiency Level' },
        { accessorKey: 'giNoGi', header: 'Gi/No Gi' },
        {
            accessorKey: 'actions',
            header: 'Acciones',
            cell: ({ row }) =>
                isEditing === row.original._id ? (
                    <>
                        <button onClick={handleSave}>Guardar</button>
                        <button onClick={handleCancel}>Cancelar</button>
                    </>
                ) : (
                    <>
                        <button onClick={() => handleEdit(row.original)}>Editar</button>
                        <button
                            onClick={() => deleteTechnique(row.original._id)}
                            style={{ color: 'red' }}
                        >
                            Eliminar
                        </button>
                    </>
                ),
        },
    ];

    const table = useReactTable({
        data: techniques,
        columns,
        getCoreRowModel: getCoreRowModel(),
    });

    return (
        <table {...table.getTableProps()} style={styles.table}>
            <thead>
                {table.getHeaderGroups().map((headerGroup) => (
                    <tr {...headerGroup.getHeaderGroupProps()}>
                        {headerGroup.headers.map((header) => (
                            <th {...header.getHeaderProps()}>
                                {flexRender(
                                    header.column.columnDef.header,
                                    header.getContext()
                                )}
                            </th>
                        ))}
                    </tr>
                ))}
            </thead>
            <tbody {...table.getTableBodyProps()}>
                {table.getRowModel().rows.map((row) => (
                    <tr {...row.getRowProps()}>
                        {row.getVisibleCells().map((cell) => (
                            <td {...cell.getCellProps()}>
                                {flexRender(cell.column.columnDef.cell, cell.getContext())}
                            </td>
                        ))}
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

const styles = {
    table: {
        width: '100%',
        borderCollapse: 'collapse',
    },
};

export default TechniqueTable;
